import React from "react";
import { StyleSheet } from "react-native";
import {
  Container,
  Header,
  Title,
  Body,
  Content,
  Text,
  Thumbnail,
  Grid,
  Row,
  Col,
  H1,
  H2,
  H3,
  Icon,
  Button
} from "native-base";

import { logout } from "@configs/fire/auth";
const uri =
  "https://www.atlassian.com/dam/jcr:ba03a215-2f45-40f5-8540-b2015223c918/Max-R_Headshot%20(1).jpg";

export default function() {
  return (
    <Container>
      <Header noLeft>
        <Body>
          <Title
            style={{
              fontWeight: "700"
            }}
          >
            Configurações
          </Title>
        </Body>
      </Header>
      <Content padder>
        <Grid>
          <Row style={styles.userInformations}>
            <Thumbnail source={{ uri }}></Thumbnail>
            <Col
              style={{
                marginLeft: 10,
                display: "flex",
                justifyContent: "center"
              }}
            >
              <H3 style={[styles.title]}>Enrico Da Silva</H3>
              <Text>Hydra Software</Text>
            </Col>
            <Col>
              <Button transparent onPress={() => logout()}>
                <Icon name="ios-log-out"></Icon>
              </Button>
            </Col>
          </Row>
        </Grid>
      </Content>
    </Container>
  );
}

const styles = StyleSheet.create({
  title: {
    fontWeight: "700"
  },
  userInformations: {
    marginTop: 50
  }
});
