import React from "react";
import {
  H1,
  Content,
  Button,
  Text,
  H3,
  Grid,
  Col,
  Row,
  Icon,
  Card,
} from "native-base";
import LoginForm from "@components/forms/Login";
import { StyleSheet } from "react-native";
import { loginGoogle, loginFacebook } from "@configs/fire/auth";
import styled from "styled-components"

const LoginContainer = styled(Content)`
  background-color: #fff;
`

const Login = () => {
  const onPress = (type: "google" | "facebook") => {
    if (type === "google") loginGoogle().catch((_e) => alert(_e));
    else loginFacebook().catch((_e) => alert(_e));
  }

  return (
    <Content padder centerContent 
      style={{ backgroundColor: "#fff" }}>
      <Grid>
        <Row>
          <Col size={10}>
            <H1 style={styles.title}> Bem vindo</H1>
            <H3>Acesse sua conta</H3>
            <Card style={styles.card}>
              <LoginForm />
              <Button dark transparent>
                <Text style={styles.button}>Esqueceu a senha?</Text>
              </Button>
              <Text style={styles.underline}>Ou acesse com sua conta:</Text>
              <Grid>
                <Row
                  style={{ alignContent: "center", alignItems: "center" }}
                >
                  <Col size={6}>
                    <Button
                      full
                      iconLeft
                      bordered
                      onPress={() => onPress("facebook")}
                    >
                      <Icon name="logo-facebook"></Icon>
                    </Button>
                  </Col>
                  <Col size={6}>
                    <Button
                      full
                      iconLeft
                      bordered
                      onPress={() => onPress("google")}
                    >
                      <Icon name="logo-google"></Icon>
                    </Button>
                  </Col>
                </Row>
              </Grid>
            </Card>
          </Col>
        </Row>
      </Grid>
    </Content>
  )
};

const styles = StyleSheet.create({
  title: {
    fontWeight: "bold",
    fontSize: 40,
    paddingTop: 30,
  },
  card: {
    padding: 10
  },
  button: {
    fontStyle: "italic",
    textDecorationLine: "underline",
  },
  underline: {
    textAlign: "center",
    paddingVertical: 10
  }
})


export default Login;
